mapper.select:
SELECT CATEGORY, BRAND_NAME, PRODUCT_NAME, COMMENT
FROM PRODUCTS_INFO
WHERE ORDER_NUM = #{orderNum}

SELECT DISTANCE
FROM ORDERS
WHERE ORDER_NUM = #{orderNum}

mapper.update:
UPDATE ORDERS
SET DELIVERY_TYPE = #{deliveryType}
WHERE ORDER_NUM = #{orderNum}

UPDATE ORDERS
SET DELIVERY_ID = #{deliveryId}, ORDER_STATUS = '배달원 접수완료'
WHERE ORDER_NUM = #{orderNum}
