mapper.select:
SELECT COUNT(id)
FROM USER_BASE
WHERE id = #{id}

SELECT ID, PASSWORD, MOBILE_NUM
FROM USER_BASE
WHERE id = #{id}
AND password = #{password}

SELECT STATUS, ID, PASSWORD, MOBILE_NUM, NAME, BIRTHDATE
FROM USER_BASE
WHERE ID = #{id}

mapper.update:
UPDATE USER_BASE
SET password = #{newPassword}
WHERE id = #{id}
